import{_ as e,c as l,o as a,az as r}from"./chunks/framework.CIUguCsn.js";const c=JSON.parse('{"title":"BioPerl (Perl5)","description":"","frontmatter":{},"headers":[],"relativePath":"comparisons/bioperl.md","filePath":"comparisons/bioperl.md","lastUpdated":null}'),i={name:"comparisons/bioperl.md"};function n(o,t,s,d,g,f){return a(),l("div",null,t[0]||(t[0]=[r('<h1 id="bioperl" tabindex="-1">BioPerl (Perl5) <a class="header-anchor" href="#bioperl" aria-label="Permalink to &quot;BioPerl (Perl5) {#bioperl}&quot;">â€‹</a></h1><ul><li><p><a href="https://bioperl.org/" target="_blank" rel="noreferrer">BioPerl</a> represents an ecosystem of related molecular biology modules written in the Perl5 programming language</p></li><li><p>Supported by the Open Bioinformatics Foundation and includes additional utilities and interfaces for common bioinformatics applications</p></li><li><p>Launched in 2002 and has contributed to major scientific achievements including the Human Genome Project</p></li></ul><p><strong>A few equivalent application tools between BioJulia and BioPerl:</strong></p><table tabindex="0"><thead><tr><th style="text-align:left;">Application</th><th style="text-align:left;">BioJulia packages</th><th style="text-align:left;">BioPerl modules/objects</th></tr></thead><tbody><tr><td style="text-align:left;">Input/Output</td><td style="text-align:left;"><a href="https://biojulia.dev/FASTX.jl/stable/" target="_blank" rel="noreferrer">FASTX</a>, <a href="https://biojulia.dev/XAM.jl/stable/" target="_blank" rel="noreferrer">XAM</a>, <a href="https://biojulia.dev/BigWig.jl/dev/" target="_blank" rel="noreferrer">BigWig</a>,...</td><td style="text-align:left;"><code>SeqIO</code></td></tr><tr><td style="text-align:left;">Sequence alignment</td><td style="text-align:left;"><a href="https://biojulia.dev/BioSequences.jl/stable/" target="_blank" rel="noreferrer">BioSequences</a>, <a href="https://biojulia.dev/BioAlignments.jl/stable/" target="_blank" rel="noreferrer">BioAlignments</a></td><td style="text-align:left;"><code>AlignIO</code>, <code>SimpleAlign</code></td></tr><tr><td style="text-align:left;">Data structures</td><td style="text-align:left;"><a href="https://biojulia.dev/Kmers.jl/stable/" target="_blank" rel="noreferrer">Kmers</a>, <a href="https://biojulia.dev/IntervalTrees.jl/stable/" target="_blank" rel="noreferrer">IntervalTrees</a></td><td style="text-align:left;"><code>Bio::Kmer</code>, <code>Bio::Tree</code></td></tr><tr><td style="text-align:left;">Population Genetics</td><td style="text-align:left;"><a href="https://biojulia.dev/PopGen.jl/" target="_blank" rel="noreferrer">PopGen</a></td><td style="text-align:left;"><code>PopGen</code></td></tr></tbody></table><p><strong>A few package/ecosystem equivalents between Julia and Perl5:</strong></p><table tabindex="0"><thead><tr><th style="text-align:left;">Application</th><th style="text-align:left;">Julia</th><th style="text-align:left;">Perl5</th></tr></thead><tbody><tr><td style="text-align:left;">Data manipulation/analysis</td><td style="text-align:left;"><a href="https://dataframes.juliadata.org/stable/" target="_blank" rel="noreferrer">DataFrames</a>, <a href="https://csv.juliadata.org/stable/" target="_blank" rel="noreferrer">CSV</a>, <a href="https://www.queryverse.org/Query.jl/stable/" target="_blank" rel="noreferrer">Query</a></td><td style="text-align:left;"><code>PDL</code>, <code>Text-CSV</code></td></tr><tr><td style="text-align:left;">Plotting/visualization</td><td style="text-align:left;"><a href="https://docs.juliaplots.org/stable/" target="_blank" rel="noreferrer">Plots</a>, <a href="http://gadflyjl.org/stable/" target="_blank" rel="noreferrer">Gadfly</a>, <a href="https://docs.makie.org/stable/" target="_blank" rel="noreferrer">Makie</a>, <a href="https://github.com/TidierOrg/TidierPlots.jl" target="_blank" rel="noreferrer">TidierPlots</a></td><td style="text-align:left;"><code>GDGraph</code>, <code>Chart-GGPlot</code></td></tr><tr><td style="text-align:left;">Statistical analysis</td><td style="text-align:left;"><a href="https://docs.julialang.org/en/v1/stdlib/Statistics/" target="_blank" rel="noreferrer">Statistics</a>, <a href="https://github.com/JuliaStats/HypothesisTests.jl" target="_blank" rel="noreferrer">HypothesisTests</a>, <a href="https://github.com/JuliaStats/GLM.jl" target="_blank" rel="noreferrer">GLM</a></td><td style="text-align:left;"><code>Statistics-Descriptive</code>, <code>PDL-Stats</code></td></tr><tr><td style="text-align:left;">Numerical mathematics</td><td style="text-align:left;"><a href="https://docs.julialang.org/en/v1/stdlib/LinearAlgebra/" target="_blank" rel="noreferrer">LinearAlgebra</a>, <a href="https://symbolics.juliasymbolics.org/stable/" target="_blank" rel="noreferrer">Symbolics</a></td><td style="text-align:left;"><code>PDL-LinearAlgebra</code>, <code>Math-Symbolic</code></td></tr><tr><td style="text-align:left;">Notebook/report generator</td><td style="text-align:left;"><a href="https://julialang.github.io/IJulia.jl/stable/" target="_blank" rel="noreferrer">IJulia</a>, <a href="https://plutojl.org/" target="_blank" rel="noreferrer">Pluto</a></td><td style="text-align:left;"><code>Devel-IPerl</code></td></tr><tr><td style="text-align:left;">Web applications</td><td style="text-align:left;"><a href="https://genieframework.com/" target="_blank" rel="noreferrer">Genie</a>, <a href="https://franklinjl.org/" target="_blank" rel="noreferrer">Franklin</a></td><td style="text-align:left;"><code>Catalyst</code>, <code>Mojolicious</code></td></tr></tbody></table><p><strong>A few notable differences between Julia and Perl5:</strong></p><table tabindex="0"><thead><tr><th style="text-align:left;">Julia</th><th style="text-align:left;">Perl5</th></tr></thead><tbody><tr><td style="text-align:left;">High-level, general-purpose compiled language</td><td style="text-align:left;">High-level, general-purpose interpreted language</td></tr><tr><td style="text-align:left;">Dynamically typed with multiple dispatch and optional type annotations</td><td style="text-align:left;">Dynamically typed without type annotations support</td></tr><tr><td style="text-align:left;">Built-in parallelism via threads, coroutines (Tasks)</td><td style="text-align:left;">Parallelism via threads, external modules (<code>Coro</code>, <code>Parallel</code>,...)</td></tr><tr><td style="text-align:left;">Lisp-inspired metaprogramming (S-expressions, homoiconicity,...)</td><td style="text-align:left;">Macro-less metaprogramming capabilities (eval, symbol table manipulation,...)</td></tr><tr><td style="text-align:left;">Single language implementation (JuliaLang)</td><td style="text-align:left;">Family of related languages/implementations (Perl5, Perl6/Raku, ActivePerl,...)</td></tr></tbody></table><p><strong>To transition from Perl5 to Julia:</strong></p><ul><li><p>Read the <a href="https://docs.julialang.org/" target="_blank" rel="noreferrer">Julia manual</a> for an in-depth look at its internals and features</p></li><li><p>See <a href="https://docs.julialang.org/en/v1/manual/running-external-programs/" target="_blank" rel="noreferrer">Running External Programs</a> in the manual for running Perl scripts or other programs from Julia</p></li></ul>',10)]))}const h=e(i,[["render",n]]);export{c as __pageData,h as default};
