import{_ as a,c as r,o as t,az as o}from"./chunks/framework.CIUguCsn.js";const f=JSON.parse('{"title":"Overview","description":"","frontmatter":{},"headers":[],"relativePath":"overview.md","filePath":"overview.md","lastUpdated":null}'),i={name:"overview.md"};function l(n,e,s,p,g,u){return t(),r("div",null,e[0]||(e[0]=[o('<h1 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview {#overview}&quot;">​</a></h1><h1 id="BioJulia:-Fast,-open,-easy-software-for-biology" tabindex="-1">BioJulia: Fast, open, easy software for biology <a class="header-anchor" href="#BioJulia:-Fast,-open,-easy-software-for-biology" aria-label="Permalink to &quot;BioJulia: Fast, open, easy software for biology {#BioJulia:-Fast,-open,-easy-software-for-biology}&quot;">​</a></h1><blockquote><p>Note: This landing site is under extensive development and will receive frequent updates. It is not in a ready state, and is published under GitHub Pages only for testing purposes.</p></blockquote><p>BioJulia is a passionate, community-led organization providing biology-related packages written in the <a href="https://julialang.org/" target="_blank" rel="noreferrer">Julia programming language</a>. The organization offers a comprehensive, fully open-source ecosystem of both libraries that serve as essential building blocks for other packages as well as interactive tools for everyday tasks and workflows.</p><p>Biologists and other scientists are fully empowered by Julia to easily tackle domain-specific challenges, taking advantage of features including:</p><ul><li><p><a href="https://pkgdocs.julialang.org/v1/environments/" target="_blank" rel="noreferrer">Fully reproducible environments</a> thanks to Julia&#39;s built-in package manager</p></li><li><p><a href="https://julialang.org/benchmarks/" target="_blank" rel="noreferrer">Competitive performance</a> that rivals that of lower-level, more complex languages such as C and Fortran</p></li><li><p><a href="https://docs.julialang.org/en/v1/manual/unicode-input/" target="_blank" rel="noreferrer">Unicode-based math symbol support</a>, <a href="https://docs.julialang.org/en/v1/stdlib/LinearAlgebra/#man-linalg" target="_blank" rel="noreferrer">transparent BLAS integration</a>, and additional features for performing complex numerical operations</p></li><li><p><a href="https://docs.julialang.org/en/v1/stdlib/REPL/#The-Julia-REPL" target="_blank" rel="noreferrer">A batteries-included read-eval-print loop (REPL)</a> for interactive data exploration and prototyping</p></li><li><p><a href="https://docs.julialang.org/en/v1/manual/calling-c-and-fortran-code/#Calling-C-and-Fortran-Code" target="_blank" rel="noreferrer">Seamless interoperability</a> <a href="https://docs.binarybuilder.org/stable/#Project-flow" target="_blank" rel="noreferrer">(JLLs,</a> <a href="https://docs.julialang.org/en/v1/manual/running-external-programs/#Running-External-Programs" target="_blank" rel="noreferrer">Cmd,...)</a> with other languages via multiple foreign function interfaces</p></li></ul><h2 id="Where-to-Start?" tabindex="-1">Where to Start? <a class="header-anchor" href="#Where-to-Start?" aria-label="Permalink to &quot;Where to Start? {#Where-to-Start?}&quot;">​</a></h2><ul><li><p>Take a look at all BioJulia code via the official <a href="https://github.com/BioJulia" target="_blank" rel="noreferrer">GitHub page</a></p></li><li><p>Begin contributing ideas and features following the <a href="https://github.com/BioJulia/Contributing.git" target="_blank" rel="noreferrer">core guidelines</a></p></li><li><p>Deep dive into the ecosystem over at the <a href="./overview">Overview</a></p></li><li><p>Start learning right away using the <a href="./overview">Getting Started tutorials</a></p></li><li><p>See some awesome examples in the <a href="./overview">BioJulia Showcase</a></p></li><li><p>Come chat with us over in the <a href="https://julialang.org/slack/" target="_blank" rel="noreferrer">Slack</a> #biology channel and on <a href="https://discourse.julialang.org/" target="_blank" rel="noreferrer">forums</a></p></li></ul><p>Use the top navigation bar to search for provided packages within one&#39;s field of interest.</p>',9)]))}const d=a(i,[["render",l]]);export{f as __pageData,d as default};
